chrome.runtime.onMessage.addListener(function(request,sender,sendResponse){if(request.action=="setTabsBadge"){updateTabsBadge(request.badge);sendResponse({result:request.badge})}else if(request.action=="getAutoLoginPswd"){sendResponse({autoLoginPswd:localStorage.getItem("autoLoginPswd")})}else{sendResponse({result:"平安无事咯！"})}});function updateTabsBadge(tabsBadge){console.log(tabsBadge);chrome.browserAction.setBadgeText({text:tabsBadge>0?""+tabsBadge:""});chrome.browserAction.setBadgeBackgroundColor({color:"#007acc"})}chrome.tabs.onSelectionChanged.addListener(function(tabId,selectInfo){chrome.tabs.sendRequest(tabId,{action:"getBdysls"})});